class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = {formData: {}, displayState: "form"}
  }

  displayBox() {
    if (this.state.displayState === "form") {
      return (<LoginBox formData={this.state.formData} displayState={this.state.displayState}/>);
    } else if (this.state.displayState === "loading") {
      return (<LoadingBox displayState={this.state.displayState}/>)
    }
  }

  render() {
    return (
      <div>
        <NavBar />
        {this.displayBox()}
        {/*Global CSS goes in here*/}
        <style jsx global>
          {`
            body {
              margin: 0px;
              background-color: #F7F7FB;
              font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;

              -webkit-font-smoothing: antialiased;
            }

          `}
        </style>
      </div>
    );
  }
}

class NavBar extends React.Component {
  render() {
    const navContainerStyle = {
      width: "100%",
      backgroundColor: "#AB1B23",
      height: "58px"
    }
    const navImgStyle = {
      width: "50px",
      marginLeft: "20px",
      marginTop: "6px"
    }
    return (
      <div style={navContainerStyle}>
        <img src="/images/icon.png" style={navImgStyle} />
      </div>
    )
  }
}

class ContentBox extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hover: false, firstName: "", lastName: "", username: "" }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }
  toggleHover() {
    this.setState({ hover: !this.state.hover });
  }
  handleChange(event) {
    const inputName = event.target.name;
    this.setState({ [inputName]: event.target.value });
  }
  handleSubmit(event) {
    event.preventDefault();
    const data = {
      firstName: this.state.firstName,
      lastName: this.state.lastName,
      username: this.state.username
    }
    this.props.formData = data;
    this.props.displayState = "loading";
    console.log(data);
    alert("Submitted");
  }
  render() {
    let styles = {
      contentBoxStyle: {
        backgroundColor: "white",
        width: "500px",
        boxShadow: "0 4px 4px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)",
        display: "block",
        margin: "0px auto",
        marginTop: "20px",
        overflow: "scroll",
        height: "450px"
      }, 
      boxTitleStyle: {
        textAlign: "center",
        paddingTop: "30px",
        margin: "0px",
        fontWeight: "400",
        fontSize: "35px"
      }, 
      innerContentStyle: {
        paddingTop: "10px",
        width: "95%",
        display: "block",
        margin: "0px auto",
        position: "relative"
      },
      subtitle: {
        textAlign: "center",
        fontWeight: "300",
        marginTop: "10px"
      },
      inputStyle: {
        border: "1px solid black",
        padding: "6px",
        borderRadius: "4px",
        fontSize: "17px",
        display: "block",
        margin: "20px auto",
        textAlign: "center",
        width: "160px"
      },
      formContainer: {
        display: "block",
        margin: "0px auto",
        width: "100%",
        marginBottom: "20px",
        marginTop: "30px"
      },
      buttonStyle: {
        fontSize: "30px",
        border: "none",
        backgroundColor: "rgb(191, 49, 56)",
        color: "white",
        width: "100px",
        height: "40px",
        cursor: "pointer",
        outline: "none",
        borderRadius: "4px",
        display: "block",
        margin: "0px auto",
        marginTop: "30px"
      }
    }
    if (this.state.hover) {
      styles.buttonStyle.backgroundColor = "#AB1B23";
    }
    return (
      <div style={styles.contentBoxStyle}>
        <h3 style={styles.boxTitleStyle}>Hey There ðŸ‘‹</h3>
        <h3 style={styles.subtitle}>Welcome to the new DMK Portal!</h3>
        <div style={styles.innerContentStyle}>
          <form onSubmit={this.handleSubmit}>
            <div style={styles.formContainer}>
              <input name="firstName" style={styles.inputStyle} value={this.state.firstName} onChange={this.handleChange} placeholder="First Name" />
              <input name="lastName" style={styles.inputStyle} value={this.state.lastName} onChange={this.handleChange} placeholder="Last Name" />
              <input name="username" style={styles.inputStyle} value={this.state.username} onChange={this.handleChange} placeholder="Purdue Username" />
            </div>
            <button onMouseLeave={() => this.toggleHover()} onMouseEnter={() => this.toggleHover()} style={styles.buttonStyle}>â†’</button>
          </form>
        </div>
      </div>
    )
  }
}

class FormBox extends React.Component {

}

export default Index;
