import { Provider as AlertProvider, useAlert } from 'react-alert'

class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = { firstName: "", lastName: "", username: "", displayState: "entry", password: "", loading: false, newUser: false }

    this.handleChange = this.handleChange.bind(this);
    this.authenticate = this.authenticate.bind(this);
    this.checkUsername = this.checkUsername.bind(this);
  }

  handleChange(event) {
    const inputName = event.target.name;
    this.setState({ [inputName]: event.target.value });
  }

  authenticate(event) {
    event.preventDefault();
    const url = "http://localhost:8080";
    fetch(url + "/authenticate?username=" + this.state.username + "&token=" + this.state.password, {
      method: "POST"
    }).then(response => response.json())
      .then(data => {
        if (data["authenticated"]) {
          document.cookie = "token=" + data.token + "; path=/";
          window.location = "/";
        } else {
          this.setState({ displayState: "error" })
        }
      })
  }

  checkUsername(event) {
    event.preventDefault();
    console.log(this.state.username)
    this.setState({ loading: true })
    const url = "http://localhost:8080/check_user?username=" + this.state.username;
    fetch(url, {
      method: 'POST'
    }).then(response => response.json())
      .then(data => {
        let newUser = false;
        let newState = data.state;
        if (data.state === "new_user") {
          newUser = true;
          newState = "authenticate"
        }
        this.setState({ displayState: newState, loading: false, newUser: newUser });
      }).catch((error) => {
        this.setState({ displayState: "error" });
      });;
  }

  validateFields() {
    return (this.state.firstName !== "" && this.state.lastName !== "" && this.state.username !== "");
  }

  displayBox() {
    if (this.state.displayState === "entry") {
      return (
        <ContentBox title={"Hey There ðŸ‘‹"} subtitle={"Welcome to the DMK Portal! Let's start with your Purdue username."}>
          <form onSubmit={this.checkUsername}>
            <Input name="username" onChange={this.handleChange} placeholder="Purdue Username" value={this.state.username} />
            <Button loading={this.state.loading} />
          </form>
        </ContentBox>
      );
    } else if (this.state.displayState === "authenticate") {
      return (
        <ContentBox title={"Check your email ðŸ“«"} subtitle={"Check your Purdue email for authentication (or continue if password saved)."}>
          <form onSubmit={this.authenticate}>
            <Input name="password" onChange={this.handleChange} value={this.state.password} placeholder="top secret..." />
            <Button loading={this.state.loading} />
          </form>
        </ContentBox>
      );
    } else {
      console.log(this.state.displayState);
      return (
        <ContentBox title={"Well, that's embarrassing ðŸ™ˆ"} subtitle={"There's an error on our end. Try again later!"}>
        </ContentBox>
      )
    }
  }

  render() {
    const alertOptions = {
      position: 'bottom center',
      timeout: 5000,
      offset: '30px',
      transition: 'scale'
    }
    return (
      <AlertProvider {...options}>
        <NavBar />
        {this.displayBox()}
        {/*Global CSS goes in here*/}
        <style jsx global>
          {`
            body {
              margin: 0px;
              background-color: #F7F7FB;
              font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;

              -webkit-font-smoothing: antialiased;
            }

          `}
        </style>
      </AlertProvider>
    );
  }
}

class NavBar extends React.Component {
  render() {
    const navContainerStyle = {
      width: "100%",
      backgroundColor: "#AB1B23",
      height: "58px"
    }
    const navImgStyle = {
      width: "50px",
      marginLeft: "20px",
      marginTop: "6px"
    }
    return (
      <div style={navContainerStyle}>
        <img src="/images/icon.png" style={navImgStyle} />
      </div>
    )
  }
}

class ContentBox extends React.Component {
  render() {
    let styles = {
      contentBoxStyle: {
        backgroundColor: "white",
        width: "500px",
        boxShadow: "0 4px 4px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)",
        display: "block",
        margin: "0px auto",
        marginTop: "20px",
        overflow: "scroll",
        height: "350px",
        maxWidth: "95%"
      },
      boxTitleStyle: {
        textAlign: "center",
        paddingTop: "30px",
        margin: "0px",
        fontWeight: "400",
        fontSize: "35px"
      },
      innerContentStyle: {
        paddingTop: "10px",
        width: "95%",
        display: "block",
        margin: "0px auto",
        position: "relative"
      },
      subtitle: {
        textAlign: "center",
        fontWeight: "300",
        marginTop: "10px",
        paddingLeft: "20px",
        paddingRight: "20px"
      }
    }

    return (
      <div style={styles.contentBoxStyle}>
        <h3 style={styles.boxTitleStyle}>{this.props.title}</h3>
        <h3 style={styles.subtitle}>{this.props.subtitle}</h3>
        <div style={styles.innerContentStyle}>
          {this.props.children}
        </div>
      </div>
    )
  }
}

class Input extends React.Component {
  render() {
    const inputStyle = {
      border: "1px solid black",
      padding: "6px",
      borderRadius: "4px",
      fontSize: "17px",
      display: "block",
      margin: "20px auto",
      textAlign: "center",
      width: this.props.width
    }
    return (
      <input name={this.props.name} style={inputStyle} value={this.props.value} onChange={this.props.onChange} placeholder={this.props.placeholder} />
    )
  }
}

class Button extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hover: false }
  }
  toggleHover() {
    this.setState({ hover: !this.state.hover });
  }
  render() {
    let buttonStyle = {
      fontSize: "30px",
      border: "none",
      backgroundColor: "rgb(191, 49, 56)",
      color: "white",
      width: "100px",
      height: "40px",
      cursor: "pointer",
      outline: "none",
      borderRadius: "4px",
      display: "block",
      margin: "0px auto",
      marginTop: "30px"
    }
    const loadingSyle = {
      width: "30px",
      display: "block",
      margin: "0px auto"
    }
    if (this.state.hover) {
      buttonStyle.backgroundColor = "#AB1B23"
    }
    if (this.props.loading) {
      return (
        <img src="/images/loading.gif" style={loadingSyle} />
      )
    } else {
      return (
        <div>
          <button onMouseLeave={() => this.toggleHover()} onMouseEnter={() => this.toggleHover()} style={buttonStyle} onClick={this.props.onClick}>â†’</button>
        </div>
      )
    }
  }
}

export default Index;
