class Index extends React.Component {
  constructor(props) {
    super(props);
    this.state = { firstName: "", lastName: "", username: "", displayState: "form" }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    const inputName = event.target.name;
    this.setState({ [inputName]: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    const url = "http://localhost:8080";
    fetch(url + "/add?first="+this.state.firstName+"&last="+this.state.lastName+"&username="+this.state.username, {
      method: "POST",
      body: JSON.stringify(this.state)
    }).then(response => response.json())
      .then(data => {
        if (data["authenticated"]) {
          this.setState({ displayState: "loading" })
        } else {
          this.setState({ displayState: "invalid" })
        }
      })
  }

  displayBox() {
    if (this.state.displayState === "form") {
      return (
        <ContentBox title={"Hey There ðŸ‘‹"} subtitle={"Welcome to the new DMK Portal!"}>
          <FormBox firstName={this.state.firstName} lastName={this.state.lastName} username={this.state.username} handleChange={this.handleChange} handleSubmit={this.handleSubmit} />
        </ContentBox>
      );
    } else if (this.state.displayState === "loading") {
      return (
        <ContentBox title={"Check your email ðŸ“«"} subtitle={"Check your Purdue email for authentication. If you didn't get anything, refresh and retype your username."}>
          <a href="/">continue</a>
        </ContentBox>
      );
    } else {
      return (
        <ContentBox title={"Uh Oh ðŸ˜•"} subtitle={"Looks like you are missing some fields. Try again."}>
          <a href="/">continue</a>
        </ContentBox>
      )
    }
  }

  render() {
    return (
      <div>
        <NavBar />
        {this.displayBox()}
        {/*Global CSS goes in here*/}
        <style jsx global>
          {`
            body {
              margin: 0px;
              background-color: #F7F7FB;
              font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;

              -webkit-font-smoothing: antialiased;
            }

          `}
        </style>
      </div>
    );
  }
}

class NavBar extends React.Component {
  render() {
    const navContainerStyle = {
      width: "100%",
      backgroundColor: "#AB1B23",
      height: "58px"
    }
    const navImgStyle = {
      width: "50px",
      marginLeft: "20px",
      marginTop: "6px"
    }
    return (
      <div style={navContainerStyle}>
        <img src="/images/icon.png" style={navImgStyle} />
      </div>
    )
  }
}

class ContentBox extends React.Component {
  render() {
    let styles = {
      contentBoxStyle: {
        backgroundColor: "white",
        width: "500px",
        boxShadow: "0 4px 4px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)",
        display: "block",
        margin: "0px auto",
        marginTop: "20px",
        overflow: "scroll",
        height: "450px",
        maxWidth: "95%"
      },
      boxTitleStyle: {
        textAlign: "center",
        paddingTop: "30px",
        margin: "0px",
        fontWeight: "400",
        fontSize: "35px"
      },
      innerContentStyle: {
        paddingTop: "10px",
        width: "95%",
        display: "block",
        margin: "0px auto",
        position: "relative"
      },
      subtitle: {
        textAlign: "center",
        fontWeight: "300",
        marginTop: "10px",
        paddingLeft: "20px",
        paddingRight: "20px"
      }
    }

    return (
      <div style={styles.contentBoxStyle}>
        <h3 style={styles.boxTitleStyle}>{this.props.title}</h3>
        <h3 style={styles.subtitle}>{this.props.subtitle}</h3>
        <div style={styles.innerContentStyle}>
          {this.props.children}
        </div>
      </div>
    )
  }
}

class FormBox extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hover: false }
  }
  toggleHover() {
    this.setState({ hover: !this.state.hover });
  }

  render() {
    let styles = {
      inputStyle: {
        border: "1px solid black",
        padding: "6px",
        borderRadius: "4px",
        fontSize: "17px",
        display: "block",
        margin: "20px auto",
        textAlign: "center",
        width: "160px"
      },
      formContainer: {
        display: "block",
        margin: "0px auto",
        width: "100%",
        marginBottom: "20px",
        marginTop: "30px"
      },
      buttonStyle: {
        fontSize: "30px",
        border: "none",
        backgroundColor: "rgb(191, 49, 56)",
        color: "white",
        width: "100px",
        height: "40px",
        cursor: "pointer",
        outline: "none",
        borderRadius: "4px",
        display: "block",
        margin: "0px auto",
        marginTop: "30px"
      }
    };
    if (this.state.hover) {
      styles.buttonStyle.backgroundColor = "#AB1B23"
    }
    return (
      <form onSubmit={this.props.handleSubmit}>
        <div style={styles.formContainer}>
          <input name="firstName" style={styles.inputStyle} value={this.props.firstName} onChange={this.props.handleChange} placeholder="First Name" />
          <input name="lastName" style={styles.inputStyle} value={this.props.lastName} onChange={this.props.handleChange} placeholder="Last Name" />
          <input name="username" style={styles.inputStyle} value={this.props.username} onChange={this.props.handleChange} placeholder="Purdue Username" />
        </div>
        <button onMouseLeave={() => this.toggleHover()} onMouseEnter={() => this.toggleHover()} style={styles.buttonStyle}>â†’</button>
      </form>
    )
  }
}

class LoadingBox extends React.Component {
  render() {
    return (<div>Loading</div>)
  }
}

class WelcomeBox extends React.Component {
  render() {
    return (<div>You're all se</div>)
  }
}

export default Index;
