import Cookies from 'js-cookie';
import Head from 'next/head'
import { Doughnut } from 'react-chartjs-2';

class Index extends React.Component {
  render() {
    const style = {
      position: "relative",
      minHeight: "100vh",
    }
    return (
      <div style={style}>
        <Head>
          <title>DMK Portal</title>
          <link href="/images/icon.png" rel="icon" />
        </Head>
        <NavBar />
        <ContentContainer />
        <style jsx global>
          {`
            body {
              margin: 0px;
              background-color: #F7F7FB;
              font-family: -apple-system,BlinkMacSystemFont,Segoe UI,Roboto,Oxygen,Ubuntu,Cantarell,Fira Sans,Droid Sans,Helvetica Neue,sans-serif;

              -webkit-font-smoothing: antialiased;
            }

            .bg-color {
              background-color: #F7F7FB;
            }

            .nav-container {
              width: 100%;
              background-color: #AB1B23;
              height: 58px;
            }

            .nav-img {
              width: 50px;
              margin-left: 20px;
              margin-top: 6px;
            }

            .content-container {
              width: 100%;
              margin: 0px auto;
              display: flex;
              justify-content: space-between;
              height: 700px;
              max-width: 1200px;
              margin-bottom: 50px;
            }

            .column {
              width: 100%;
            }

            .content-box {
              background-color: white;
              width: 90%;
              box-shadow: 0 4px 4px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
              display: block;
              margin: 0px auto;
              margin-top: 20px;
              overflow: scroll;
            }

            .box-title {
              text-align: center;
              padding-top: 10px;
              margin: 0px;
              font-weight: 400;
              font-size: 20px;
            }

            .box-content {
              padding-top: 10px;
              width: 95%;
              display: block;
              margin: 0px auto;
              position: relative;
            }

            p {
              text-align: center;
              color: grey;
              font-weight: 300;
              margin-top: 3px;
              margin-bottom: 3px;
            }

            .attendance-graph {
              padding-top: 0px;
              margin: 0px auto;
              display: block;
              width: 40%;
              max-width: 200px;
              margin-bottom: 10px;
            }

            .spotlight-img:hover {
              box-shadow: 4px 4px 4px 0 rgba(0, 0, 0, 0.2), -10px 6px 20px 0 rgba(0, 0, 0, 0.19);
              cursor: pointer;
            }

            .spotlight-img {
              width: 43%;
              margin: 0px auto;
              display: block;
              margin-bottom: 25px;
              margin-top: 20px;
              border-radius: 50%;
              transition-duration: 0.2s;
              min-width: 120px;
            }

            .brother-description {
              transition-duration: 0.2s;
            }

            .brother-description:hover {
              cursor: pointer;
              color: rgb(50, 50, 50);
            }

            .events-container {
              margin-top: 40px;
            }

            .event-wrapper {
              min-height: 70px;
              position: relative;
            }

            .event-item {
              margin-top: 0px;
              display: flex;
              justify-content: flex-start;
              transition-duration: 0.2s;
              cursor: pointer;
              border-radius: 10px;
              position: relative;
              top: 50%;
              transform: translateY(-50%);
              padding: 10px;
              width: calc(100% - 20px);
            }

            .event-item:hover {
              background-color: rgb(235, 235, 235);
            }

            .news-item-container {
              display: flex;
              justify-content: flex-start;
              transition-duration: 0.2s;
              padding-top: 7px;
              padding-bottom: 7px;
              padding-right: 2px;
              border-radius: 10px;
              cursor: pointer;
              min-height: 50px;
            }

            .news-item-container:hover {
              background-color: rgb(235, 235, 235);
            }

            .item-img-wrapper {
              width: 30%;
              position: relative;
            }

            .item-img {
              position: relative;
              width: 80%;
              left: 50%;
              top: 50%;
              transform: translate(-50%, -50%);
            }

            .item-text-wrapper {
              width: 70%;
              position: relative;
            }

            .item-content {
              position: relative;
              top: 50%;
              transform: translateY(-70%);
            }

            .hr {
              border: 0.6px solid rgb(215, 215, 215);
              margin: 3px auto;
            }

            .modal {
              /* Hidden by default */
              position: fixed;
              /* Stay in place */
              z-index: 1;
              /* Sit on top */
              padding-top: 100px;
              /* Location of the box */
              left: 0;
              top: 0;
              width: 100%;
              /* Full width */
              height: 100%;
              /* Full height */
              overflow: auto;
              /* Enable scroll if needed */
              background-color: rgb(0, 0, 0);
              /* Fallback color */
              background-color: rgba(0, 0, 0, 0.4);
              /* Black w/ opacity */
            }

            /* Modal Content */

            .modal-content {
              background-color: #fefefe;
              margin: auto;
              border: 1px solid white;
              width: 80%;
              max-width: 500px;
              position: relative;
              padding-bottom: 40px;
              padding-top: 3%;
            }

            .modal-img {
              width: 40%;
              display: block;
              margin: 0px auto;
              min-width: 150px;
              max-width: 80%;
            }

            .modal-text-wrapper {
              width: 77%;
              padding-left: 10%;
              padding-right: 10%;
              margin: 0px auto;
              display: block;
            }

            .modal-title {
              text-align: center;
            }

            .modal-text {
              text-align: left;
            }

            /* The Close Button */

            .close {
              color: #aaaaaa;
              float: right;
              font-size: 35px;
              font-weight: bold;
              position: absolute;
              right: 10px;
              top: 0px;
            }

            .close:hover, .close:focus {
              color: #000;
              text-decoration: none;
              cursor: pointer;
            }

            .modal-btn {
              text-align: center;
              display: block;
              margin: 0px auto;
              margin-top: 30px;
              height: 30px;
              font-size: 16px;
            }

            .date-container {
              width: 50px;
              height: 50px;
              background-color: #AB1B23;
              border-radius: 10px;
            }

            .event-name {
              margin-top: 5px;
              margin-left: 10px;
              font-weight: 300;
              margin-bottom: 0px;
            }

            .event-time {
              text-align: left;
              margin-left: 10px;
            }

            .month {
              margin-top: 3px;
              margin-bottom: 0px;
              color: white;
              font-size: 12px;
            }

            .day {
              color: white;
              margin-top: 0px;
              font-size: 25px;
            }

            .input-wrapper {
              width: 90%;
              display: block;
              margin: 0px auto;
              position: relative;
            }

            .slack-input {
              background-color: rgb(242, 242, 242);
              resize: none;
              font-size: 17px;
              width: 92%;
              height: 70px;
              border: none;
              border-radius: 4px;
              margin-bottom: 30px;
              padding: 10px;
            }

            button {
              font-size: 17px;
              border: none;
              background-color: rgb(191, 49, 56);
              color: white;
              width: 80px;
              height: 25px;
              cursor: pointer;
              outline: none;
              border-radius: 4px;
            }

            .slack-send {
              position: absolute;
              bottom: 0;
              right: 3px;
            }

            button:hover {
              background-color: #AB1B23;
            }


            @media only screen and (max-width: 790px) {
              .content-container {
                display: block;
                max-width: 500px;
                height: auto;
              }
              .content-box {
                max-height: 400px;
              }
              .slack-input {
                margin-bottom: 50px;
              }
              .slack-send {
                bottom: 20px;
                right: 10px;
              }
            }

          `}
        </style>
      </div>
    );
  }
}

class NavBar extends React.Component {
  logout() {
    Cookies.remove("token");
    window.location = "/login"
  }
  render() {
    const styles = {
      logoutStyle: {
        position: "absolute",
        right: "20px",
        width: "25px",
        top: "15px",
        cursor: "pointer"
      }
    }
    return (
      <div className="nav-container">
        <img src="/images/icon.png" className="nav-img" />
        <img src="/images/logout.png" style={styles.logoutStyle} onClick={() => { this.logout() }} />
      </div>
    )
  }
}

class ContentContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = { eventsData: [], newsData: [], upcomingData: [], spotlightData: {}, brotherData: {}, loadedPage: false }
  }

  componentDidMount() {
    if (Cookies.get("token") == undefined) {
      window.location = "/login"
    } else {
      this.setState({ token: Cookies.get("token") });
    }
    this.fetchPageData();
  }

  fetchPageData() {
    const url = "http://localhost:8080/pageData?token=" + Cookies.get("token");
    fetch(url).then(res => res.json())
      .then((data) => {
        this.setState({
          spotlightData: data.body.brotherSpotlight,
          upcomingData: data.body.upcomingData,
          newsData: data.body.newsData,
          eventsData: data.body.eventsData,
          brotherData: data.body.brotherData,
          loadedPage: true
        })
      });
  }

  render() {
    if (!this.state.loadedPage) {
      const loadingStyle = {
        display: "block",
        margin: "0px auto",
        marginTop: "20%",
        transform: "translateY(-50%)",
        width: "30px"
      }
      return (
        <img src="/images/loading.gif" style={loadingStyle} />
      )
    }
    return (
      <div className="content-container">
        <div className="column">
          <ContentBox title={"Welcome, " + this.state.brotherData.firstName + " ðŸ‘‹"} height="7%" />
          <ContentBox title="Chapter Attendance ðŸ™Œ" height="45%">
            <Attendance data={this.state.brotherData} checkInActive={true} />
          </ContentBox>
          <ContentBox title="Brother Spotlight ðŸ¤ " height="40%">
            <Spotlight data={this.state.spotlightData} />
          </ContentBox>
        </div>
        <div className="column">
          <ContentBox title="Coming Up âš¡ï¸" height="44.7%">
            <NewsList data={this.state.upcomingData} />
          </ContentBox>
          <ContentBox title="News Panel ðŸ“°" height="50%">
            <NewsList data={this.state.newsData} />
          </ContentBox>
        </div>
        <div className="column">
          <ContentBox title="Upcoming Events ðŸ“£" height="67.7%">
            <EventsList data={this.state.eventsData} />
          </ContentBox>
          <ContentBox title="Drop us a Line ðŸ¤–" height="27%">
            <Slack />
          </ContentBox>
        </div>
      </div>
    )
  }
}

class ContentBox extends React.Component {
  render() {
    return (
      <div className="content-box" style={{ "height": this.props.height }}>
        <h3 className="box-title">{this.props.title}</h3>
        <div className="box-content">
          {this.props.children}
        </div>
      </div>
    )
  }
}

class Attendance extends React.Component {
  constructor(props) {
    super(props);
    this.state = { displayModal: false }
  }
  checkIn() {
    this.toggleModal();
    console.log("clicked");
    const url = "https://api.airtable.com/v0/appwaUv9OXdJ4UNpy/attendance?api_key=" + this.props.apiKey;
    //fetch(url);
  }
  toggleModal = () => {
    this.setState({ displayModal: !this.state.displayModal });
  }
  renderButton() {
    const descriptionStyle = {
      marginTop: "30px",
      marginBottom: "30px"
    }
    const buttonStyle = {
      display: "block",
      margin: "0px auto",
      marginTop: "30px",
      height: "30px",
      width: "100px",
      fontSize: "18px"
    }
    if (this.props.checkInActive) {
      const modalSubtitle = {
        textAlign: "center"
      }
      const inputStyle = {
        border: "1px solid black",
        padding: "6px",
        borderRadius: "4px",
        fontSize: "17px",
        display: "block",
        margin: "20px auto",
        textAlign: "center",
        width: this.props.width
      }
      return (
        <div>
          <button onClick={() => this.checkIn()} style={buttonStyle}>check in</button>
          <Modal display={this.state.displayModal} closeModal={this.toggleModal}>
            <div className="modal-text-wrapper">
              <h2 className="modal-title">{"Check In âœ…"}</h2>
              <p style={modalSubtitle} className="modal-text">{"Please enter the code provided in chapter"}</p>
              <input style={inputStyle} value={this.props.code} onChange={this.props.onChange} placeholder={this.props.placeholder} />
            </div>
            <button className="modal-btn" onClick={this.toggleModal}>close</button>
          </Modal>
        </div>
      );
    } else {
      return (
        <p style={descriptionStyle}>We've seen you at {this.props.data.attendance} out of 5 chapters this semester.</p>
      );
    }
  }
  render() {
    return (
      <div>
        <Graph attended={this.props.data.attendance} totalChapters="5" />
        {this.renderButton()}
      </div>
    );
  }
}

